angular.module("ui.bootstrap.demo").controller("ModalDemoCtrl",function(n,e,t){n.items=["item1","item2","item3"],n.animationsEnabled=!0,n.open=function(o){var i=e.open({animation:n.animationsEnabled,templateUrl:"myModalContent.html",controller:"ModalInstanceCtrl",size:o,resolve:{items:function(){return n.items}}});i.result.then(function(e){n.selected=e},function(){t.info("Modal dismissed at: "+new Date)})},n.toggleAnimation=function(){n.animationsEnabled=!n.animationsEnabled}}),angular.module("ui.bootstrap.demo").controller("ModalInstanceCtrl",function(n,e,t){n.items=t,n.selected={item:n.items[0]},n.ok=function(){e.close(n.selected.item)},n.cancel=function(){e.dismiss("cancel")}});