describe("uibIsClass",function(){var e;beforeEach(module("ui.bootstrap.isClass")),beforeEach(inject(function(t,i){e=i,e.activeClass="active",e.items=[1,2,3],element=t('<div><div ng-repeat="item in items" uib-is-class="activeClass for activeItem on item">{{ item }}</div></div>')(e),e.$digest()})),it("initializes classes correctly",function(){expect(element.find(".active").length).toEqual(0)}),it("sets classes correctly",function(){e.activeItem=2,e.$digest(),expect(element.find(".active").text()).toEqual("2"),e.items.splice(1,1),e.$digest(),expect(element.find(".active").length).toEqual(0)}),it("handles removal of items correctly",function(){e.activeItem=2,e.$digest(),expect(element.find(".active").text()).toEqual("2"),e.items.splice(1,1),e.$digest(),expect(element.find(".active").length).toEqual(0),e.activeItem=1,e.$digest(),expect(element.find(".active").text()).toEqual("1")}),it("handles moving of items",function(){e.activeItem=2,e.items=[2,1,3],e.$digest(),expect(element.find(".active").text()).toEqual("2"),expect(element.find(".active").length).toEqual(1),expect(element.find(".active").index()).toEqual(0),e.items=[4,3,2],e.$digest(),expect(element.find(".active").text()).toEqual("2"),expect(element.find(".active").length).toEqual(1),expect(element.find(".active").index()).toEqual(2)}),it("handles emptying and re-adding the items",function(){e.activeItem=2,e.items=[],e.$digest(),expect(element.find(".active").length).toEqual(0),e.items=[4,3,2],e.$digest(),expect(element.find(".active").text()).toEqual("2"),expect(element.find(".active").index()).toEqual(2)}),it("handles undefined items",function(){e.activeItem=void 0,e.items=[],e.$digest(),expect(element.find(".active").length).toEqual(0),e.items=[4,3,void 0],e.$digest(),expect(element.find(".active").length).toEqual(1),expect(element.find(".active").text()).toEqual("")})});