describe("typeaheadHighlight",function(){var t,e,o,r;beforeEach(module("ui.bootstrap.typeahead")),beforeEach(inject(function(t,a){e=t,o=a,r=spyOn(e,"warn")})),beforeEach(inject(function(e){t=e})),it("should higlight a match",function(){expect(o.getTrustedHtml(t("before match after","match"))).toEqual("before <strong>match</strong> after")}),it("should higlight a match with mixed case",function(){expect(o.getTrustedHtml(t("before MaTch after","match"))).toEqual("before <strong>MaTch</strong> after")}),it("should higlight all matches",function(){expect(o.getTrustedHtml(t("before MaTch after match","match"))).toEqual("before <strong>MaTch</strong> after <strong>match</strong>")}),it("should do nothing if no match",function(){expect(o.getTrustedHtml(t("before match after","nomatch"))).toEqual("before match after")}),it("should do nothing if no or empty query",function(){expect(o.getTrustedHtml(t("before match after",""))).toEqual("before match after"),expect(o.getTrustedHtml(t("before match after",null))).toEqual("before match after"),expect(o.getTrustedHtml(t("before match after",void 0))).toEqual("before match after")}),it("issue 316 - should work correctly for regexp reserved words",function(){expect(o.getTrustedHtml(t("before (match after","(match"))).toEqual("before <strong>(match</strong> after")}),it("issue 1777 - should work correctly with numeric values",function(){expect(o.getTrustedHtml(t(123,"2"))).toEqual("1<strong>2</strong>3")}),it("should show a warning when this component is being used unsafely",function(){t("<i>before</i> match after","match"),expect(r).toHaveBeenCalled()})});